from scapy.all import *
from time import sleep

while True:

    # Define the IP layer
    ip = IP(src="3.80.0.0", dst="43.143.0.0")  # Spoof source and destination IPs

    # Define the UDP layer
    udp = UDP(sport=12345, dport=53)  # Set source and destination ports

    # Define the payload (data) of the packet
    payload = ""

    data = bytes.fromhex(payload)
    # Combine the layers to form the packet
    packet = ip / udp / data

    # Send the packet
    send(packet)
    sleep(10)
